    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}{{parent()}} Connexion{% endblock %}

{% block content %}
<form method=\"post\" id=\"signup\">
    {% if error %}
        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    {% for message in app.flashes('notice') %}
        <div class=\"alert alert-info\">{{ message }}</div>
    {% endfor %}

    {% if app.user %}
        <div class=\"mb-3\">
            Connecté en tant que {{ app.user.userIdentifier }}, <a href=\"{{ path('app_logout') }}\">Déconnexion</a>
        </div>
    {% endif %}

    <h1 class=\"h3 mb-3 font-weight-normal\">Connexion</h1>
    <div class=\"form-group col-6 pl-0\">
        <label for=\"inputEmail\">Email</label>
        <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\" autocomplete=\"email\" required autofocus>
        <span class=\"error\" aria-live=\"polite\"></span>
    </div>

    <div class=\"form-group col-6 pl-0\">
        <label for=\"inputPassword\">Mot de passe</label>
        <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\"
               autocomplete=\"current-password\" required>
        <span class=\"error\" aria-live=\"polite\"></span>
    </div>

    <input type=\"hidden\" name=\"_csrf_token\"
           value=\"{{ csrf_token('authenticate') }}\"
    >

    {#
        Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
        See https://symfony.com/doc/current/security/remember_me.html

        <div class=\"checkbox mb-3\">
            <label>
                <input type=\"checkbox\" name=\"_remember_me\"> Remember me
            </label>
        </div>
    #}

    <button class=\"btn btn-lg btn-primary\" type=\"submit\">Se connecter</button>
</form>
    <p class=\"mt-3\"><a href=\"{{ path('app_reset_password') }}\">Mot de passe oublié</a> ?</p>

    <script src=\"{{ asset('assets/js/custom2.js') }}\" defer></script>

{% endblock %}
", "security/login.html.twig", "C:\\laragon\\www\\testAuth\\templates\\security\\login.html.twig");
